import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Right.css";

export default class Rop3 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           
        };
    }
   
  getROption3 = () => {
		let op = {
			 xAxis: {
				type: 'category',
				data: ['14:30', '', '15:30', '', '16:30', '', '17:30'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					}
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
				}
			},
			yAxis: {
				type: 'value',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度	
				}
			},
			series: [{
				data: [20, 60, 50, 70, 80, 60, 80],
				type: 'line',
				areaStyle: {
					 normal: {
						color: new Echarts.graphic.LinearGradient(0, 0, 0, 1, [{
								offset: 0,
								color: '#8ec6ad'
							}, {
								offset: 1,
								color: '#ffe'
							}])
					}
				},
				itemStyle : { 
					normal : { 
						color:'#8cd5c2', //改变折线点的颜色
						lineStyle:{ 
						color:'#00FFFF' //改变折线颜色
						} 
					} 
				}
			}]
		}
		return op;		
	}
    render() {
        return (
            <div className="aa">
				<ReactEcharts option={this.getROption3()} style={{height:'200px',width:'260px'}}></ReactEcharts>
			</div>
        )
    }
}
