import React from "react";
import style from './../BroadcastTable.module.css';
import { message, Popconfirm } from 'antd';
import CreateFileModal from './CreateFileModal'
import ModifyModal from './ModifyModal';
import { wholeTerminal, groupTerminal } from './../Terminals';

export default class LiveBroadcast extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           
        };
    }
   
   getROption3 = () => {
		let op = {
			 xAxis: {
				type: 'category',
				data: ['14:30', '', '15:30', '', '16:30', '', '17:30'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
					
                    rotate:30
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
			},
			yAxis: {
				type: 'value',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
			},
			series: [{
				data: [20, 60, 50, 70, 80, 60, 80],
				type: 'line'
			}]
		}
		return op;		
	}

    render() {
        return (
            <div className="a">
						<ReactEcharts option={this.getOption3()} style={{height:'200px',width:'260px'}}></ReactEcharts>
			</div>
        )
    }
}
