import React, { PureComponent } from 'react';
import EyeIcon from "../Image/eyeIcon.png";
import datas from "./statics.json";
import style from "./emergencyTable.module.css";

export default class Even extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            mes: ''
	   };
    }
	
	render() {
        const getData = this.state.datas.map((item, index) => {
            if(index < this.props.mes){
                return (
                    <tr key={index}>
					<td>{item.selectedNameValue}</td>
					<td>{item.selectedTypeValue}</td>
					<td>{item.selectedLevelValue}</td>
					<td>{item.selectedStatusValue}</td>
					<td><img src={EyeIcon} alt="" style={{width:'15px',height:'15px'}}/></td>
                </tr>
                )
            }else {
                return (
                    <tr key={index}>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                    </tr>
                )
            }
        });

        if(this.props.mes < 5) {
            return(
                <div style={{
                    textAlign: 'center',
                    color: '#ccc',
                }}>暂无该种类型设备报修</div>
            )
        }else{
            <div className={style.emergencyDown}>
				<div className={style.emergencyTitle}>应急事件列表</div>
				<div className={style.emergencyTable}>
					<table >
						<thead>
							<tr className={style.tableTitle}>
								<th>应急事件名称</th>
								<th>类型</th>
								<th>等级</th>
								<th>状态</th>
								<th>查看</th>
							</tr>
						</thead>
						<tbody  className={style.tableBody}>
							{this.getData}
						</tbody>
					</table>
				</div>
            </div>
        }
    }
}
