import React, { PureComponent } from 'react';
import echarts from 'echarts/lib/echarts';
// 引入柱状图
import  'echarts/lib/chart/bar';
// 引入提示框和标题组件
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';

export default class HomeChartLeft extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {};
    }
	 componentDidMount() {
        // 基于准备好的dom，初始化echarts实例
        var myChart = echarts.init(document.getElementById('main'));
        // 绘制图表
        myChart.setOption({
			color:['#0000CC','#0033CC','#0066CC','#0099CC','#00CCCC'],
            angleAxis: {
				type: 'category'
			},
			radiusAxis: {
				
			},
			polar: {
			},
			series: [{
				type: 'bar',
				data: [33],
				coordinateSystem: 'polar',
				name: '在库文物数量',
				stack: 'a'
		
			}, {
				type: 'bar',
				data: [25],
				coordinateSystem: 'polar',
				name: '布展文物数量',
				stack: 'a'
			}, {
				type: 'bar',
				data: [12],
				coordinateSystem: 'polar',
				name: '外出巡展文物数量',
				stack: 'a'
			},{
				type: 'bar',
				data: [90],
				coordinateSystem: 'polar',
				name: '外部文物数量',
				stack: 'a'
			},{
				type: 'bar',
				data: [55],
				coordinateSystem: 'polar',
				name: '馆内文物展览数量',
				stack: 'a'
			}],
			legend: {
				show: true,
				data: ['在库文物数量', '布展文物数量', '外出巡展文物数量', '外部文物数量', '馆内文物展览数量']
			}
        });
    }
    render() {
        return (
            
				 <div id="main" style={{ width: 400, height: 400 }}></div>
           
        );
    }
}
