import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Right.css";


class Rop2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
	
	getROption2 = (params) => {
		
		let getSeries = () => {
			let radius = 20;
			let arr = [];
		
			for(let i=0;i<params.length;i++){
				arr.push(
					{
						name: params.name[i],
						type:'pie',
						radius: [radius + '%', radius+10+'%'],
						avoidLabelOverlap: false,
						label: {
							normal: {
								show: false,
								position: 'center'
							},
						   
						},
						labelLine: {
							normal: {
								show: false
							}
						},
						data:[
							{value:params.data[i], name:params.name[i]},
							{value:100-params.data[i], name:'aa'}
							
						]
					}
				)
				radius += 10;
			}
			return arr;
		}
		let getStatics ={
            legend: {
				orient: 'vertical',
				x: 'left',
				data:['在库文物数量','布展文物数量','外出巡展文物数量','外部文物数量','馆内文物展览数量']
			},
			series: getSeries()
        }
        return getStatics
		
    }
    render() {
        return (
            <div className="aa">
                <ReactEcharts option={this.getROption2({ data: [33, 25, 37, 90, 55], name: ['在库文物数量','布展文物数量','外出巡展文物数量','外部文物数量','馆内文物展览数量'] })} style={{ height: "50%" }}></ReactEcharts>
            </div>
        )
    }
}

export default Rop2;
