import React, { PureComponent } from 'react';
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import { Select, Icon } from 'antd';
import style from "./TitleDropDown.module.css";
import "./Title.css";

const { Option } = Select;

export default class TitleCompent extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
			date: new Date()
        };
		this.handleChange = this.handleChange.bind(this);
    }
	handleChange(event) {
        this.setState({value: event.target.value});
    }
		
	componentDidMount = () => {
		this.timerID = setInterval(
			() => this.tick(),
			   1000
		);
	}

	componentWillUnmount = () => {
		clearInterval(this.timerID);
	}
		
	tick() {
		this.setState({
		  date: new Date()
		});
	}
	
	
	
    render() {
		const icon = <Icon type="caret-down" style={{ color: "rgb(0, 83, 229)" }} />
        return (
            <div id="title">
				<div className="icon">
					<img src={ReturnIcon}/>
					<img src={RefreshIcon}/>
					<img src={WholeIcon}/>
				</div>
				<div className="menue">
					<div>您好，01号管理员。</div>
					<div style={{display: 'flex'}}>
						<div style={{fontSize:'1.5em'}}>场馆服务</div>
						<Select value={this.state.value} size="small" suffixIcon={icon} onChange={this.handleChange} style={{paddingTop: '11%'}}>
							<option value="能耗计量">能耗计量</option>
							<option value="楼宇自控">楼宇自控</option>
							<option value="报警故障管理">报警故障管理</option>
						</Select>
					</div>
					<div style={{display: 'flex',justifyContent:'flex-end'}}>
						<div style={{width:'50%',lineHeight:'1.5em',textAlign:'center',paddingTop:'5%'}}>{this.state.date.toLocaleString()}</div>
						<div style={{fontSize:'2em'}}>33°</div>
						<div style={{display: 'flex',flexDirection: 'column',lineHeight:'1.5em',paddingTop:'5%'}}>
							<div>风级：3级</div>
							<div>PM值：优</div>
						</div>
					</div>
				</div>	
            </div>
        );
    }
}
