import React, { PureComponent } from 'react';
import  "../../../util";
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import TitleDropDown from "./TitleDropDown1.js"
import "./Title.css"

export default class TitleCompent extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
			value: 'basketball'
        };
		  this.handleChange = this.handleChange.bind(this);
    }
	handleChange(event) {
        this.setState({value: event.target.value});
    }
    render() {
        return (
            <div id="title">
				<div className="icon">
					<img src={ReturnIcon}/>
					<img src={RefreshIcon}/>
					<img src={WholeIcon}/>
				</div>
				<div className="menue">
					<div>
                        <label>choose favorite sportsï¼š
                            <select value={this.state.value} onChange={this.handleChange}>
                                <option value="running">running</option>
                                <option value="basketball">basketball</option>
                                <option value="skiing">skiing</option>
                            </select>
                        </label>
                        <div>chosen: {this.state.value}</div>
                    </div>
				</div>	
            </div>
        );
    }
}
