import React, { PureComponent } from 'react';
import ReactEcharts from 'echarts-for-react';
import "./Right.css";

export default class HomeChartRight extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
        };
    }
	getROption3 = () => {
		let op = {
			 xAxis: {
				type: 'category',
				data: ['14:30', '', '15:30', '', '16:30', '', '17:30']
			},
			yAxis: {
				type: 'value'
			},
			series: [{
				data: [20, 60, 50, 70, 80, 60, 80],
				type: 'line'
			}]
		}
		return op;		
	}
	getROption4 = () => {
		let op = {
			xAxis: {
				type: 'category',
				data: ['4-17', '', '4-19', '', '4-21', '', '4-23'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
					interval:0,
                    rotate:30
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
				
			},
			yAxis: {
				type: 'value',
				name: '数量/天',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
			},
			series: [{
				data: [120, 200, 150, 80, 70, 110, 130],
				type: 'bar',
				barWidth: '35%',
				itemStyle: {
					normal: {
						color: function(p){
							let colorList = ["#0066FF","#0099FF","#00CCFF","#00FFFF"];
							return colorList[p.dataIndex];
						},
						label: {
							show: true, //开启显示
							position: 'top', //在上方显示
							textStyle: { //数值样式
								color: '#00FFFF'
							}
						}
					}
				}
			}]
		}
		return op;		
	}
	
    render() {
        return (
			<div id="right">
				<div className="r3">
					<div className="rtitle">能源数据统计</div>
					<div className="aa">
						<ReactEcharts option={this.getROption3()} style={{height:'200px',width:'250px'}}></ReactEcharts>
					</div>
				</div>
				<div className="r4">
					<div className="rtitle">系统费用统计</div>
					<div className="aa">
						<ReactEcharts option={this.getROption4()} style={{height:'200px',width:'250px'}}></ReactEcharts>
					</div>
				</div>
			</div>
        );
    }
}
