import React from "react";
import ReactEcharts from 'echarts-for-react';

export default class Integrity extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
	
    getOption = () => {
		let option = {
			
			series : [
				{
					type: 'pie',
					radius : '55%',
					center: ['50%', '60%'],
					data:[
						{value:25, name:'完整'},
						{value:75, name:'残缺'},
					],
					itemStyle: {
						color: ['#0099FF','00FFFF'],
					}
				}
			]
		}
		return option
	}
    render() {
        return (
			<ReactEcharts option={this.getOption()} style={{height:'90%',width:'100%'}}></ReactEcharts>
        )
    }
}
