import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Right.css";


class Rop2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
	
	getROption2 = () => {
		let op = {
			 yAxis: [{
                type: 'category',
                inverse: true,
                min: -8,
                axisLine: {
                    show: false
                },
                axisTick: {
                    show: false
                },
                axisLabel: {
                    interval: 0,
                    inside: true,
                    textStyle: {
                        color: "rgb(28,244,249)",
                        fontSize: 12,
                    },
                    show: true
                },
            }],
            xAxis: [{
                show: false
            }],
            series: [
			{
				    name:'访问来源',
					type:'pie',
					radius: ['30%', '50%'],
					avoidLabelOverlap: false,
					label: {
						normal: {
							show: false,
							position: 'center'
						},
					   
					},
					labelLine: {
						normal: {
							show: false
						}
					},
					data:[
						{value:335, name:'直接访问'},
						{value:310, name:'邮件营销'},
						{value:234, name:'联盟广告'},
						{value:135, name:'视频广告'},
						{value:1548, name:'搜索引擎'}
					]
			}],
		}
		return op;		
	}
   
    render() {
        return (
            <div>
                <ReactEcharts option={this.getROption2()} style={{height:'200px',width:'260px'}}></ReactEcharts>   
            </div>
        )
    }
}

export default Rop2;
