import React, { PureComponent } from 'react';
import  "../../../util";
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import TitleDropDown from "./TitleDropDown.js"
import "./Title.css"

export default class TitleCompent extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
			date: new Date(),
			options: ['能耗计量','楼宇自控','警报故障管理']
        };
    }
	
		
	componentDidMount = () => {
		this.timerID = setInterval(
			() => this.tick(),
			   1000
		);
	}

	componentWillUnmount = () => {
		clearInterval(this.timerID);
	}
		
	tick() {
		this.setState({
		  date: new Date()
		});
	}
	
	
	
	
	
    render() {
        return (
            <div id="title">
				<div className="icon">
					<img src={ReturnIcon}/>
					<img src={RefreshIcon}/>
					<img src={WholeIcon}/>
				</div>
				<div className="menue">
				
				</div>	
            </div>
        );
    }
}
