import React, { PureComponent } from 'react';
import  "../../../util";
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import TitleDropDown from "./TitleDropDown.js"
import "./Title.css"

export default class Title extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
			date: new Date()
        };
    }
	componentDidMount = () => {
		this.timerID = setInterval(
			() => this.tick(),
			   1000
		);
	}
	componentWillUnmount = () => {
		clearInterval(this.timerID);
	}	
	tick() {
		this.setState({
		  date: new Date()
		});
	}

    render() {
        return (
            <div id="title">
				<div className="icon">
					<img src={ReturnIcon}/>
					<img src={RefreshIcon}/>
					<img src={WholeIcon}/>
				</div>
				<div className="menue">
					<div style={{width:'25%'}}>您好，01号管理员。</div>
					<div className="mtitle">{this.props.title}</div>
					<TitleDropDown></TitleDropDown>
					<div style={{display: 'flex',width:'30%'}}>
						<div style={{width:'50%',lineHeight:'1.5em',textAlign:'center',paddingTop:'5%'}}>{new Date().Format("yyyy年MM月dd日 EEE HH:mm:ss")}</div>
						<div style={{fontSize:'2em'}}>33°</div>
						<div style={{display: 'flex',flexDirection: 'column',lineHeight:'1.5em',paddingTop:'5%'}}>
							<div>风级：3级</div>
							<div>PM值：优</div>
						</div>
					</div>
				</div>	
            </div>
        );
    }
}
