import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Left.css";

export default class Top1 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
	
   getOption1 = (params) => {
		let getSeries = () => {
		    let radius = 20;
			let arr = [];
			let colorList = ['#0033CC', '#0066CC', '#0099CC', '#00CCCC', '#00FFCC'];
			for(let i = 0; i < params.data.length; i++){
				arr.push(
					{
						name: params.name[i],
						type:'pie',
						hoverAnimation: false,
						radius: [radius + '%', radius+10+'%'],
						avoidLabelOverlap: false,
						label: {
							normal: {
								show: false,
								position: 'center'
							},
						   
						},
						labelLine: {
							normal: {
								show: false
							}
						},
						data:[
							{
								value:params.data[i], 
								name:params.name[i],
								itemStyle: {
									normal:{
										//自定义颜色
										color: colorList[i]
									}
								}
							},
							{
								value:100-params.data[i], 
								name:'aa',
								itemStyle: {
									normal:{
										color: 'rgba(0,0,0,0)'
									}
								}
							}
						]
					}
				)
				radius += 10;
			}
			return arr;
		} 
        return {
			legend: {
				orient: 'vertical',
				x: 'left',
				data: params.names,
				textStyle: { //图例文字的样式
                    color: '#00FFCC',
                    fontSize: 12
                }
			},
			series: getSeries()
		}
	}
    render() {
        return (
            <div className="a">
				<ReactEcharts option={this.getOption1({ data: [33, 25, 12, 90, 55], which: "down", name: ["在库文物数量", "布展文物数量", "外出巡展文物数量", "外部文物数量", "馆内文物展览数量"] })} style={{height:'200px',width:'260px'}}></ReactEcharts>
			</div>
        )
    }
}
