import React from 'react';
import ReactEcharts from "echarts-for-react";

class EmergencyAnalyze extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    getOption = (params) => {
        let getStatics = () => {
            let res = [];
            let radius = 60;
            let colorList = ["rgb(28,244,249)", "rgb(0,138,238)", "rgb(0,83,229)"];
            for (let i = 0; i < params.data.length; i++) {
                res.push(
                    {
                        type: 'pie',
                        clockWise: false,
                        center: ["25%", "55%"],
                        hoverAnimation: false,   //饼-鼠标移入均不放大
                        radius: [radius + "%", radius + 20 + "%"],
                        data: [
                            {
                                value: params.data[i],
                                name: params.names[i],
                                label: {
                                    show: false,
                                    position: "inside",
                                    formatter: () => {
                                        return params.data[i];
                                    }
                                },
                                labelLine: {
                                    show: false,
                                    length: 0,
                                    length2: 0
                                },
                                itemStyle: {
                                    normal: {
                                        color: colorList[i]
                                    }
                                }
                            },
                            {
                                value: 5000- params.data[i],
                                name: 'invisible',
                                itemStyle: {
                                    normal: {
                                        color: 'rgba(0,0,0,0)',
                                        label: { show: false },
                                        labelLine: { show: false }
                                    },
                                    emphasis: {
                                        color: 'rgba(0,0,0,0)'
                                    }
                                }
                            }
                        ]
                    }
                );
                radius += 10;
            }
            return res;            
        }
        return {
            width: "85%",
            height: "95%",
            legend: {
                orient: 'vertical',
                data: params.names,
                itemHeight: 12,
                right: 'right',
                y: 'bottom',
                textStyle: {
                    fontSize: 12,
                    color: ["rgb(28,244,249)", "rgb(0,138,238)", "rgb(0,83,229)"]
                }
            },
            grid: {
                top: '-53%',
                left: "24%",
                containLabel: false
            },
            yAxis: [{
                type: 'category',
                inverse: true,
                min: -8,
                axisLine: {
                    show: false
                },
                axisTick: {
                    show: false
                },
                axisLabel: {
                    interval: 0,
                    inside: true,
                    textStyle: {
                        color: "rgb(28,244,249)",
                        fontSize: 12,
                    },
                    show: true
                },
                data: params.data
            }],
            xAxis: [{
                show: false
            }],
            series: getStatics()
        }
    }
    render() {
        return (
            <div style={{ height: "90%", width: "90%" }}>
                <ReactEcharts
                    option={this.getOption({ data: [1134, 2560, 3378], names: ["本日总耗能(单位kw/h)", "本日总负荷(单位kw/h)", "本日总电费(单位kw/h)"] })}
                    style={{ height: "50%" }}
                ></ReactEcharts>
               
            </div>
        )
    }
}
export default EmergencyAnalyze;
