import React from "react";
import { Carousel, Icon, Button } from 'antd';
import JingDong from "./Image/jingDong.png"
import MaFeng from "./Image/maFeng.png"
import MeiTuan from "./Image/meiTuan.png"
import QuNa from "./Image/quNa.png"
import TongCheng from "./Image/tongCheng.png"
import TuNiu from "./Image/tuNiu.png"
import XieCheng from "./Image/xieCheng.png"
import style from "./otaComment.module.css";


export default class OTAComment extends React.Component {
    constructor(props) {
        super(props);
		this.state = {   
			data:[387017, 283809, 214887, 256545 ,58561, 21206, 20731],
			image: [ QuNa, XieCheng, MeiTuan, MaFeng, TongCheng, JingDong, TuNiu ]
        };
		
        this.next = this.next.bind(this);
        this.prev = this.prev.bind(this);
    }
		next() {
			this.slider.slick.slickNext();
		}
		prev() {
			this.slider.slick.slickPrev();
		}
    render() {
		const content = this.state.data.map((item,index)=>{
			return (
				<div  className={style.otaContent}>
					<img src={this.state.image[index]} alt="" style={{width:'10%'}}/>
					<div className="b">口碑热度</div>
					<div className="c"></div>
					<div className="d">{item}</div>
					<div className="e">满意度</div>
				</div>
			)
		});
		
        return (
			<div className="to"> 
				<Carousel dots= 'false' autoplay = 'false' ref={el => (this.slider = el)}>
					<div style={{display:'flex'}}>{content}</div>
					<div className={style.otaTotal}>{content}</div>
				</Carousel>
			</div>
        )
    }
}
