import React from "react";
import ReactEcharts from 'echarts-for-react';
import style from './EnergyStatistic.module.css';

export default class EnergyStatistics extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           isToggleOn: true
        };
    }
	handleClick(index) {
		this.setState(prevState => ({
			isToggleOn: !prevState.isToggleOn
		}));
	}
 
  getOption = () => {
		let op = {
			width: "80%",
            height: "65%",
			grid: {
                top: "20%",
                left: "12%"
            },
			 xAxis: {
				type: 'category',
				data: ['14:30', '', '15:30', '', '16:30', '', '17:30'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					}
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
				}
			},
			yAxis: {
				type: 'value',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度	
				}
			},
			series: [{
				data: [20, 60, 50, 70, 80, 60, 80],
				type: 'line',
				areaStyle: {
					normal: {
						color: {
							type: 'linear',
							x: 0,
							y: 0,
							x2: 0,
							y2: 1,
							colorStops: [{
								offset: 0, color: '#00FFFF' // 0% 处的颜色
							}, {
								offset: 1, color: 'rgba(0,0,0,0.4)' // 100% 处的颜色
							}],
							global: false // 缺省为 false
						}
					}
				},
				itemStyle : { 
					normal : { 
						color:'#8cd5c2', //改变折线点的颜色
						lineStyle:{ 
							color:'#00FFFF'//改变折线颜色
						} 
					} 
				}
			}]
		}
		return op;		
	}
    render() {
         return (
			<div>
				<div className={style.enTop}>
					<button className={style.btn} onClick={(index)=>this.handleClick(1)} style={{backgroundColor: this.state.isToggleOn ? 'rgba(0,51,255,0.3)' : '#0066FF'}}>能耗</button>
					<button className={style.btn} onClick={(index)=>this.handleClick(2)} style={{backgroundColor: this.state.isToggleOn ? 'rgba(0,51,255,0.3)' : '#0066FF'}}>负荷</button>
					<button className={style.btn} onClick={(index)=>this.handleClick(3)} style={{backgroundColor: this.state.isToggleOn ? 'rgba(0,51,255,0.3)' : '#0066FF'}}>电费</button>
				</div>
				<ReactEcharts option={this.getOption()} style={{ height: "90%", width: "100%" }}/>
			</div>
            
        )
    }
}
