import React, { PureComponent } from 'react';




export default class ExpertsManagement extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            names: []
        };
    }
	componentDidMount() {
		 this.getName()
     }
    getName(){
		let name = this.state.names;
		for(let i=0;i<99;i++){
			name.push("XXX名称"+i);
		}
		this.setState({
			names: name
		});
	}
    render() {
        return (
            <div style={{color:'red'}}>{this.state.names}</div>
        )
    }
}
