import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Left.css";

export default class Top4 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
	
	getOption4 = () => {
		let op = {
			// 图表的位置
			grid: {
				position: 'center',
			 },
			radar: {
				shape: 'polygon',
				splitNumber: 5, // 雷达图圈数设置
				name: {
					textStyle: {
						color: '#0066FF',
					},
				},
				// 设置雷达图中间射线的颜色
				axisLine: {
					lineStyle: {
						color: '#0066FF',
						},
				},
				indicator: [
				   { name: '美食', max: 6500},
				   { name: '酒店', max: 16000},
				   { name: '服务', max: 30000},
				   { name: '体验', max: 38000},
				   { name: '风景', max: 52000},
				   { name: '交通', max: 25000},
				   { name: '其他', max: 16000},
				   { name: '服务', max: 30000},
				   { name: '体验', max: 38000},
				   { name: '风景', max: 52000},
				   { name: '交通', max: 25000}
				   
				],
				//雷达图背景的颜色，在这儿随便设置了一个颜色，完全不透明度为0，就实现了透明背景
				splitArea : {
					show : false,
					areaStyle : {
						color: '#0066FF', // 图表背景的颜色
					},
				},
				splitLine : {
					show : true,
					lineStyle : {
						width : 1,
						color : '#0066FF', // 设置网格的颜色
					},
				},
			},
			series: [{
				name: '雷达图', // tooltip中的标题
				type: 'radar', //表示是雷达图
				symbol: 'circle', // 拐点的样式，还可以取值'rect','angle'等
				symbolSize: 8, // 拐点的大小

				areaStyle: {
					normal: {
						width: 1,
						opacity: 0.2,
					},
				},
				data: [
					{
						value: [17, 24, 27, 29, 26, 16, 13, 17, 25],
						name: '2018-07',
						// 设置区域边框和区域的颜色
						itemStyle: {
							normal: {
								color: 'rgba(255,225,0,.3)',
								lineStyle: {
									color: 'rgba(255,225,0,.3)',
								},
							},
						},
						//在拐点处显示数值
						label: {
							normal: {
								show: true,
								formatter: (params: any) => {
									return params.value
								},
							},
						},
					},
					{
						value: [5, 20, 19, 11, 22, 17, 8, 19, 16],
						name: '',
						 itemStyle: {
							normal: {
								color: 'rgba(60,135,213,.3)',
								lineStyle: {
									width: 1,
									color: 'rgba(60,135,213,.3)',
								},
							},
						},
					},
					 {
						value: [7, 18, 19, 13, 22, 17, 8, 25, 9],
						name: '',
						itemStyle: {
							normal: {
								color: 'rgba(255,74,74,.3)',
								lineStyle: {
									width: 1,
									color: 'rgba(255,74,74,.3)',
								},
							},
						},
					},
				],
			}]

		}
		return op;
	}
    render() {
        return (
           
			<ReactEcharts option={this.getOption4()} style={{height:'100%',width:'100%'}}></ReactEcharts>

        )
    }
}
