import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Left.css";

export default class Top2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
	
   getOption2 = () => {
		let op = {
			xAxis: {
				type: 'value',
				name: '人数',
				show: false,
				
			},
			yAxis: {
				type: 'category',
				data: ['温度', '湿度', 'TVOC', '甲醛', 'PM2.5', '氨气'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
					interval:0
				},
				axisLine: {
					show: false
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
				},
				
			},
			grid: {
                left: '20%',
				bottom: '40%'
            },
			series: [{
				data: [120, 200, 150, 80],
				type: 'bar',
				barWidth: '35%',
				itemStyle: {
					normal: {
						color: function(p){
							let colorList = ["#0066FF","#0099FF","#00CCFF","#00FFFF"];
							return colorList[p.dataIndex];
						},
						label: {
							show: true, //开启显示
							position: 'top', //在上方显示
							textStyle: { //数值样式
								color: '#00FFFF'
							}
						}
					}
				}
			}]
		}
		return op
	}
    render() {
        return (
            <div className="a">
				<ReactEcharts option={this.getOption2()} style={{height:'230px',width:'260px'}}></ReactEcharts>
			</div>
        )
    }
}
