import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Right.css";

export default class Rop3 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           
        };
    }
   
  getROption3 = () => {
		let op = {
			 xAxis: {
				type: 'category',
				data: ['14:30', '', '15:30', '', '16:30', '', '17:30'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
					
                    rotate:30
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
			},
			yAxis: {
				type: 'value',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
			},
			series: [{
				data: [20, 60, 50, 70, 80, 60, 80],
				type: 'line'
			}]
		}
		return op;		
	}
    render() {
        return (
            <div className="aa">
				<ReactEcharts option={this.getROption3()} style={{height:'100px',width:'200px'}}></ReactEcharts>
			</div>
        )
    }
}
