import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Right.css";


class Rop2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
	
	getROption2 = () => {
		let op = {
			 tooltip: {
			trigger: 'item',
			formatter: "{a} <br/>{b}: {c} ({d}%)"
		},
		legend: {
			 orient: 'vertical',
             itemHeight: 12,
             right: 'right',
             y: 'bottom',
             textStyle: {
                fontSize: 12,
                color: ["rgb(28,244,249)", "rgb(0,138,238)", "rgb(0,83,229)"]
             },
			data:['在','邮件营销','联盟广告','视频广告','搜索引擎']
		},
		series: [
			{
				name:'访问来源',
				type:'pie',
				radius: ['60%', '90%'],
				avoidLabelOverlap: false,
				label: {
					normal: {
						show: false,
						position: 'center'
					},
				   
				},
				labelLine: {
					normal: {
						show: false
					}
				},
				data:[
					{value:335, name:'直接访问'},
					{value:310, name:'邮件营销'},
					{value:234, name:'联盟广告'},
					{value:135, name:'视频广告'},
					{value:1548, name:'搜索引擎'}
				]
			},
			{
				name:'访',
				type:'pie',
				radius: ['30%', '50%'],
				avoidLabelOverlap: false,
				label: {
					normal: {
						show: false,
						position: 'center'
					},
				   
				},
				labelLine: {
					normal: {
						show: false
					}
				},
				data:[
					{value:335, name:'直接访问'},
					{value:1000-355, name:'aaa'}
					
				]
			}
			]
		}
		return op;		
	}
   
    render() {
        return (
            <div>
                <ReactEcharts option={this.getROption2()} style={{height:'200px',width:'260px'}}></ReactEcharts>   
            </div>
        )
    }
}

export default Rop2;
