import React, { PureComponent } from 'react';
import ReactEcharts from 'echarts-for-react';
import "./Left.css";

export default class HomeChartLeft extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {};
    }
	
    getOption = () => {
		let option = {
			
			xAxis: {
				type: 'category',
				data: ['应入馆团体人数', '应入馆散客人数', '已入馆团体人数', '已入馆散客人数'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
					interval:0,
                    rotate:30
				},
				
			},
			yAxis: {
				type: 'value',
				name: '人数',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				}
			},
			series: [{
				data: [120, 200, 150, 80],
				type: 'bar',
				barWidth: '35%',
				itemStyle: {
					normal: {
						color: function(p){
							let colorList = ["#0066FF","#0099FF","#00CCFF","#00FFFF"];
							return colorList[p.dataIndex];
						}
					}
				}
			}]
		}
		return option
	}
    render() {
        return (
            <div class="a">
				<div class="title">游客实时数据</div>
				<ReactEcharts option={this.getOption()} style={{height:'220px',width:'280px'}}></ReactEcharts>
            </div>
        );
    }
}
