import React from "react";
import ReactEcharts from 'echarts-for-react';
import "./Left.css";

export default class Top2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
	
   getOption2 = () => {
		let op = {
			tooltip : {
			trigger: 'axis',
			axisPointer : {            // 坐标轴指示器，坐标轴触发有效
				type : 'shadow'        // 默认为直线，可选为：'line' | 'shadow'
			}
		},
		grid: {
			left: '50px',
			right: '70px'
		},
		xAxis:  {
			type: 'value',
			name: '人数',
			show: false,
		},
		yAxis: [{
			type: 'category',
			data: ['温度', '湿度', 'TVOC', '甲醛', 'PM2.5', '氨气'],
			extraCssText: 'box-shadow: 0 0 3px write;',
			axisLabel: {  
				textStyle:{
					color:'#0066FF',  //坐标的字体颜色
				},
				interval:0
			},
			axisLine: {
				show: false
			},
			axisTick:{
				show:false, //隐藏坐标轴的刻度
			}
		},
		{
			type: 'category',
			data: ['18°', '90%', '0.3mg/m³', '0.5mg/m³', '66ug/m³.5', '12%'],
			extraCssText: 'box-shadow: 0 0 3px write;',
			axisLabel: {  
				textStyle:{
					color:'#00FFFF',  //坐标的字体颜色
				},
				interval:0
			},
			axisLine: {
				show: false
			},
			axisTick:{
				show:false, //隐藏坐标轴的刻度
			}
		}
		],
		series: [
			{
				name: '直接访问',
				type: 'bar',
				stack: '总量',
				barWidth: '100%',
				itemStyle: {
					normal: {
						barBorderRadius: 5,
						color: '#00CCFF'
					}
				},
				data: [120, 200, 150, 80 , 100, 60]
			},
			{
				type: 'bar',
				stack: '总量',
				barWidth: '100%',
				itemStyle: {
					normal: {
						color: 'rgba(255,255,255,0.4)'
					}			
				},
				data: [180, 100, 150, 220, 200, 240]
			}]
		}
		return op
	}
    render() {
        return (
           
				<ReactEcharts option={this.getOption2()} style={{height:'100%',width:'100%',border: '1px solid red'}}></ReactEcharts>
		
        )
    }
}
