import React, { PureComponent } from 'react';
import  "../../../util";
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import "./Title.css"

export default class TitleCompent extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
        };
    }
	getDate = ()=>{
		let d = new Date();
		let date = d.toLocaleString();
		return date;
	}
    render() {
        return (
            <div id="title">
				<div className="icon">
					<img src={ReturnIcon}/>
					<img src={RefreshIcon}/>
					<img src={WholeIcon}/>
				</div>
				<div className="menue">
					<div>您好，01号管理员。</div>
					<div style={{fontSize:'1.5em'}}>博物馆智慧运营指挥中心</div>
					<div style={{display: 'flex'}}>
						<div style={{width:'10%'}}>{this.getDate()}</div>
						<div style={{fontSize:'2em'}}>33°</div>
						<div style={{display: 'flex',flexDirection: 'column',lineHeight:'1.5em',paddingTop:'10%'}}>
							<div>风级：3级</div>
							<div>PM值：优</div>
						</div>
					</div>
				</div>	
            </div>
        );
    }
}
