import React, { PureComponent } from 'react';
import ReactEcharts from 'echarts-for-react';

export default class HomeChartRight extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
        };
    }
	getOption1 = () => {
		let op = {
			
			xAxis: {
				type: 'category',
				data: ['4-17', '', '4-19', '', '4-21', '', '4-23'],
				axisLabel: {  
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
					interval:0,
                    rotate:30
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
				
			},
			yAxis: {
				type: 'value',
				name: '数量/天',
				axisLabel: {
					textStyle:{
						color:'#0066FF',  //坐标的字体颜色
					},
				},
				splitLine:{
					show:false,  //去掉背景的网格线
				},
				axisLine:{
					lineStyle:{
						color:'#0066FF',  //坐标轴的颜色
					},
				},
				axisTick:{
					show:false, //隐藏坐标轴的刻度
					
				}
			},
			series: [{
				data: [120, 200, 150, 80, 70, 110, 130],
				type: 'bar',
				barWidth: '35%',
				itemStyle: {
					normal: {
						color: function(p){
							let colorList = ["#0066FF","#0099FF","#00CCFF","#00FFFF"];
							return colorList[p.dataIndex];
						},
						label: {
							show: true, //开启显示
							position: 'top', //在上方显示
							textStyle: { //数值样式
								color: '#00FFFF'
							}
						}
					}
				}
			}]
		}
		return op
	}
	 getOption2 = () => {
		let op = {
			
			tooltip: {},
			legend: {
				data: ['能力值']
			},
			radar: {
				name: {
					textStyle: {
						color: '#0066FF',
						
					}
				},
				indicator: [
					{name: '文化', max: 100},
					{name: '生活', max: 100},
					{name: '娱乐', max: 100},
					{name: '流行', max: 100},
					{name: '科学', max: 100}
				]
			},
			series: [{
				name: '能力',
				type: 'radar',
				areaStyle: {normal: {}},
				data: [
					{name: "能力值", value: [80, 90, 94, 85, 88]}
				]
			}]
		}
		return op
	}
	
    render() {
        return (
			<div>
				<div class="title">游客实时数据</div>
				<div class="a">
					<ReactEcharts option={this.getOption1()} style={{height:'220px',width:'280px'}}></ReactEcharts>
				</div>
				<div class="title">游客实时数据</div>
				<div class="a">
					<ReactEcharts option={this.getOption2()} style={{height:'220px',width:'280px'}}></ReactEcharts>
				</div>
			</div>
        );
    }
}
