import React from "react";
import ReactEcharts from 'echarts-for-react';

export default class TotalNumbers extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
	
   getOption = (params) => {
		let getSeries = () => {
		    let radius = 20;
			let arr = [];
			let colorList = ['#0033CC', '#0066CC', '#0099CC', '#00CCCC', '#00FFCC'];
			for(let i = 0; i < params.data.length; i++){
				arr.push(
					{
						name: params.name[i],
						type:'pie',
						tooltip: {
							show: true,                 // 是否显示提示框
							formatter: '{b} </br> 销量{c}件 </br> 占比{d}%'      // 提示框显示内容,此处{b}表示各数据项名称，此项配置为默认显示项，{c}表示数据项的值，默认不显示，({d}%)表示数据项项占比，默认不显示。
						},

						radius: [radius + '%', radius+10+'%'],
						avoidLabelOverlap: false,
						
						data:[
							{
								value:params.data[i], 
								name:params.name[i],
								itemStyle: {
									normal:{
										//自定义颜色
										color: colorList[i]
									}
								}
							},
							{
								value: 100-params.data[i], 
								name: 'a',
								itemStyle: {
									normal:{
										color: 'rgba(0,0,0,0)'
									}
								}
							}
						]
					}
				)
				radius += 10;
			}
			return arr;
		} 
        return {
			color: ['#0033CC', '#0066CC', '#0099CC', '#00CCCC', '#00FFCC'],
			series: getSeries()
		}
	}
    render() {
        return (
			<ReactEcharts option={this.getOption({ data: [90, 65, 50, 25, 10], which: "down", name: ["数量全部", "年统计", "月统计", "7日统计", "今日统计"] })} style={{height:'90%',width:'100%'}}></ReactEcharts>
        )
    }
}
