import React from "react";
import { Progress } from 'antd';
import style from './Scene.module.css';

export default class SceneEnvironment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
        };
    }
 
    render() {
        return (
		<div>
			<div className={style.step}>
				<div className={style.environmentTitle}>
					<div>温度</div>
					<div>湿度</div>
					<div>TVOC</div>
					<div>甲醛</div>
					<div>PM2.5</div>
					<div>氨</div>
				</div>
				<div className={style.environmentContent}>
					<Progress percent={50} showInfo={false}/>
					<Progress strokeColor={{from: '#0033FF',to: '#00CCFF',}} percent={90} size="small" showInfo={false}/>
					<Progress strokeColor={{from: '#0033FF',to: '#00CCFF',}} percent={30} size="small" showInfo={false}/>
					<Progress strokeColor={{from: '#0033FF',to: '#00CCFF',}} percent={50} size="small" showInfo={false}/>
					<Progress strokeColor={{from: '#0033FF',to: '#00CCFF',}} percent={66} size="small" showInfo={false}/>
					<Progress strokeColor={{from: '#0033FF',to: '#00CCFF',}} percent={12} size="small" showInfo={false}/>
				</div>
				<div className={style.environmentData}>
					<div>18°</div>
					<div>90%</div>
					<div>0.3mg/m³</div>
					<div>0.5mg/m³</div>
					<div>66ug/m³.5</div>
					<div>12%</div>
				</div>
			</div>
			<div className={style.enTop}>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 0) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" }}>能耗</button>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 1) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" }}>负荷</button>
			</div>
		</div>
        )
    }
}
