import React, { PureComponent } from 'react';
import ReactEcharts from 'echarts-for-react';


export default class HomeChartLeft extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {};
    }
	
    getOption = () => {
		let option = {
			xAxis: {
				type: 'category',
				data: ['应入馆团体人数', '应入馆散客人数', '已入馆团体人数', '已入馆散客人数'],
				axisLabel: {  
					interval:0,
                    rotate:40
				}
			},
			yAxis: {
				type: 'value',
				name: '人数'
			},
			series: [{
				data: [120, 200, 150, 80],
				type: 'bar',
				barWidth: '40%'
			}]
		}
		return option
	}
    render() {
        return (
            <div class="a">
				<ReactEcharts option={this.getOption()} style={{height:'220px',width:'200px'}}></ReactEcharts>
            </div>
        );
    }
}
