import React, { PureComponent } from 'react';
import ReactEcharts from 'echarts-for-react';


export default class HomeChartLeft extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {};
    }
	
    getOption = () => {
		const option = {
			xAxis: {
				type: 'category',
				data: ['应入馆团体人数', '应入馆散客人数', '已入馆团体人数', '已入馆散客人数']
			},
			yAxis: {
				type: 'value',
				name: '人数'
			},
			series: [{
				data: [120, 200, 150, 80, 70, 110, 130],
				type: 'bar'
			}]
		}
		return option
	}
    render() {
        return (
            
			<ReactEcharts option={option} style={{height:'220px',width:'100%'}}></ReactEcharts>
           
        );
    }
}
