import React, { PureComponent } from 'react';
import  "../../../util";
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import style from "./Title.module.css"

export default class Title extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
			date: new Date()
        };
    }
	componentDidMount = () => {
		this.timerID = setInterval(
			() => this.tick(),
			   1000
		);
	}
	componentWillUnmount = () => {
		clearInterval(this.timerID);
	}	
	tick() {
		this.setState({
		  date: new Date()
		});
	}

    render() {
        return (
            <div>
				<div className={style.icon}>
					<img src={ReturnIcon} alt="" style={{width:'7%'}}/>
					<img src={RefreshIcon} alt=""/>
					<img src={WholeIcon} alt=""/>
				</div>
				<div className={style.menue}>
					<div className={style.menuueTitle}>{this.props.title}</div>
					<div className={style.mTitle}>您好，01号管理员。</div>
					<div className={style.mRight}>
						<div>{new Date().Format("yyyy年MM月dd日 EEE HH:mm:ss")}</div>
						<div>33°</div>
						<div>
							<div>风级：3级</div>
							<div>PM值：优</div>
						</div>
					</div>
				</div>	
            </div>
        );
    }
}
