import React, { PureComponent } from 'react';
import  "../../../util";
import ReturnIcon from "./img/returnIcon.png"
import RefreshIcon from "./img/refreshIcon.png"
import WholeIcon from "./img/wholeIcon.png"
import "./Title.css"

export default class TitleCompent extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
			date: new Date()
        };
    }
	
		
	componentDidMount = () => {
		this.timerID = setInterval(
			() => this.tick(),
			   1000
		);
	}

	componentWillUnmount = () => {
		clearInterval(this.timerID);
	}
		
	tick() {
		this.setState({
		  date: new Date()
		});
	}
	
	
	
    render() {
        return (
            <div id="title">
				<div className="icon">
					<img src={ReturnIcon}/>
					<img src={RefreshIcon}/>
					<img src={WholeIcon}/>
				</div>
				<div className="menue">
					<div style={{fontSize:'1em',width:'20%'}}>您好，01号管理员。</div>
					<div style={{fontSize:'1em',width:'20%'}}>博物馆智慧运营指挥中心</div>
					<div style={{display: 'flex',justifyContent:'flex-end',width:'20%'}}>
						<div style={{width:'50%',lineHeight:'1.5em',textAlign:'center',paddingTop:'5%'}}>{this.state.date.toLocaleString()}</div>
						<div style={{fontSize:'2em'}}>33°</div>
						<div style={{display: 'flex',flexDirection: 'column',lineHeight:'1.5em',paddingTop:'5%'}}>
							<div>风级：3级</div>
							<div>PM值：优</div>
						</div>
					</div>
				</div>	
            </div>
        );
    }
}
