import React from "react";
import ConditionerIcon from "../Image/conditionerIcon.png"
import style from './EquipmentOperation.module.css';
class EquipmentOperation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    render() {
        return (
		<div>
			<div className={style.enTop}>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 0) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" }}>空调机组</button>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 1) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" }}>摄像头机组</button>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 2) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" ,marginTop: '4%'}}>水泵机组</button>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 3) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" ,marginTop: '2%'}}>传感器</button>
			</div>
            <div className={style.equipmentBottom}>
                <div>数量：<img src={ConditionerIcon} alt="" style={{width:'20%',height: '30%'}}/></div>
			    <div >
					<div style={{width:'40%'}}>空调机组运行状态:</div>  
					<div className={style.equipmentState}>
						<span>[</span><span style={{color: '#00CCFF',marginLeft: '10%',marginRight: '10%'}}>333</span><span>]</span>
						<div className={style.equipmentRunning}>运行</div>
					</div>
				</div>
            </div>
		</div>
        )
    }
}
export default EquipmentOperation;
