import React from "react";
import ReactEcharts from 'echarts-for-react';

export default class TicketTypes extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           
        };
    }
  getOption = () => {
	let op = {
			legend: {
				orient: 'vertical',
				x: 'left',
				data:['直接访问','邮件营销','联盟广告','视频广告','搜索引擎']
			},
			series: [
				{
					name:'访问来源',
					type:'pie',
					radius: ['50%', '70%'],
					avoidLabelOverlap: false,
					label: {
						normal: {
							show: false,
							position: 'center'
						},
						emphasis: {
							show: true,
							textStyle: {
								fontSize: '30',
								fontWeight: 'bold'
							}
						}
					},
					labelLine: {
						normal: {
							show: false
						}
					},
					data:[
						{value:335, name:'直接访问'},
						{value:310, name:'邮件营销'},
						{value:234, name:'联盟广告'},
						{value:135, name:'视频广告'},
						{value:1548, name:'搜索引擎'}
					]
				}
			]
		}
		return op;		
	}
    render() {
        return (
			<ReactEcharts option={this.getOption()} style={{height:'100%',width:'100%'}}></ReactEcharts>
        )
    }
}
