import React, { PureComponent } from 'react';
import EyeIcon from "../Image/eyeIcon.png";
import datas from "./statics.json";
import style from "./emergencyTable.module.css";
export default class Event extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            data: datas
	   };
    }
	getDate=()=> {
		let table = [];
		for(let i=0;i<this.state.data.length;i++){
			table.push(
				 <tr className={style.commandGroupList}>
					<td>this.state.data[i].selectedNameValue</td>
					<td>this.state.data[i].selectedTypeValue</td>
					<td>this.state.data[i].selectedLevelValue</td>
					<td>this.state.data[i].selectedStatusValue</td>
					<td><img src={EyeIcon} alt="" style={{width:'1em',height:'1em'}}/></td>
                </tr>
			);
		}
		return table;
	}
    render() {
        return (
           <div className={style.memberList}>
				<div className={style.expertsName}>应急事件列表</div>
                <table className={style.table}>
                    <thead>
                        <tr className={style.tableTitle}>
                            <th>应急事件名称</th>
                            <th>类型</th>
                            <th>等级</th>
							<th>状态</th>
							<th>查看</th>
                        </tr>
                    </thead>
                    <tbody >
                        {this.getDate()}
                    </tbody>
                </table>
            </div>
        )
    }
}
