import React from "react";
import { Progress } from 'antd';
import style from './Scene.module.css';

var indoor: [30,73,0.6,0.1,35,25];
var outdoor: [18,90,30,50,66,12];
export default class SceneEnvironment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {   
			selectButton: 0,
            selectChoice: indoor
        };
    }
 
    handleButtonClick =(e)=> {
		switch (e.target.innerText) {
            case "室内": {
                this.setState({
                    selectButton: 0,
                    selectChoice: indoor
					
                })
                break;
            }
            case "室外": {
                this.setState({
                    selectButton: 1,
                    selectChoice: outdoor	
                })
                break;
            }
            default: { }
        }
	}
	
	getData =()=> {
		let arr = [];
		for(let i=0;i<this.state.selectChoice.length;i++) {
			arr.push(<div className={style.stepContent}><Progress strokeColor={{from: '#0033FF',to: '#00CCFF',}} percent={this.state.selectChoice[i]} showInfo={false} /></div>);
		}
		console.log(arr);
		return arr
	}
    render() {
        return (
		<div>
			<div className={style.enTop}>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 0) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" }}>室内</button>
				<button className={style.btn} onClick={this.handleButtonClick} style={(this.state.selectButton === 1) ? { background: "#0066FF" } : { background: "rgba(2,48,115,0.6)" }}>室外</button>
			</div>
			<div className={style.step}>
				<div className={style.environmentTitle}>
					<div>温度</div>
					<div>湿度</div>
					<div>TVOC</div>
					<div>甲醛</div>
					<div>PM2.5</div>
					<div>氨</div>
				</div>
				<div className={style.environmentContent}>{this.getData()}</div>
				<div className={style.environmentData}>
					<div>18°</div>
					<div>90%</div>
					<div>0.3mg/m³</div>
					<div>0.5mg/m³</div>
					<div>66ug/m³.5</div>
					<div>12%</div>
				</div>
			</div>
			
		</div>
        )
    }
}
