import React from "react";
import OTACommentImg from './OTACommentImg.js';
import { Carousel, Icon, Button } from 'antd';
import "./Content.css";


export default class OTAComment extends React.Component {
    constructor(props) {
        super(props);
        this.next = this.next.bind(this);
        this.prev = this.prev.bind(this);
    }
		next() {
			this.slider.slick.slickNext();
		}
		prev() {
			this.slider.slick.slickPrev();
		}
    render() {
        return (
			<div className="to"> 
				<Carousel dots= 'false' autoplay = 'false' ref={el => (this.slider = el)}>
					<OTAComment></OTAComment>
					<OTAComment></OTAComment>	
				</Carousel>
				<div>
					<Button className="but1" type="primary" ghost="true" size="small" onClick={this.prev}><Icon type="left" /></Button>
					<Button className="but2" type="primary " ghost="true" size="small" onClick={this.next}><Icon type="right" /></Button>
				</div>
			</div>
        )
    }
}
